import { Anuncio } from './../../../Models/Anuncio';
import { CategoriaService } from './../../../Services/CategoriaService';
import { EmpresaService } from './../../../Services/EmpresaService';
import { AnuncioService } from 'src/app/Services/AnuncioService';
import { Empresa } from './../../../Models/Empresa';
import { Component, Injectable, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CategoriaAnuncio } from 'src/app/Models/CategoriaAnuncio';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-cadastrar-anuncio',
  templateUrl: './cadastrar-anuncio.component.html',
  styleUrls: ['./cadastrar-anuncio.component.css'],
})

@Injectable()

export class CadastrarAnuncioComponent implements OnInit {
  form: FormGroup;
  categorias: CategoriaAnuncio[] = [];
  empresaId: number = 0;
  categoriaId: number = 0;
  localUrl: any;
  file?: File;
  nomeArquivo: string = '';
  imagemUrl = "../../../../assets/images/icons/upload.png";
  public arquivoSelecionado: File | null | undefined;

  constructor(
    private fb: FormBuilder,
    private anuncioservice: AnuncioService,
    private categoriaService: CategoriaService,
    private toast: ToastrService
  ) {
  }

  ngOnInit(): void {
    this.validation();
    this.BuscarCategorias();
  }

  get f() {
    return this.form.controls;
  }

  onChange(event: any) {
    const selectedFiles = <FileList>event.srcElement.files;
    document.getElementById('imageLabel')!.innerHTML = selectedFiles[0].name;
  }

  public validation(): void{
    this.form = this.fb.group({
      descricao: ['',[Validators.required, Validators.minLength(4), Validators.maxLength(150)]],
      data_validade: ['', Validators.required],
      preco: ['', Validators.required],
      categoria: ['', Validators.required],
      imagem: ['', Validators.required],
    });
  }

  salvarAnuncio() {
    debugger;
    this.categoriaId = parseInt(
      (<HTMLSelectElement>document.getElementById('categoriaId')).value
    );
    this.empresaId = 3;

    let anuncio = {
      descricao: this.f.descricao.value,
      data_validade: this.f.data_validade.value,
      preco: this.f.preco.value,
      ativo: true,
      is_destaque: false,
      categoria_id: this.categoriaId,
      empresa_id: this.empresaId,
      imagem_url: this.nomeArquivo,
    };

    this.anuncioservice.SalvarAnuncio(anuncio)
    .subscribe(
      () => {
        this.toast.success('Anúncio cadastrado com sucesso!', 'Sucesso');
      },
      (erro) => {
        this.toast.error('Erro ao cadastrar o anúncio.', 'Erro');
      }
    );
  }

  BuscarCategorias() {
    this.categoriaService.BuscarTodasCategorias().subscribe(
      (result) => {
        if (result != null) {
          this.categorias = result;
        }
      },
      () => {
        alert(
          `Nenhuma categoria encontrada. Verifique a conexão com a internet.`
        );
      }
    );
  }

  onFileChange(ev :any) : void{

    const reader = new FileReader();
    reader.onload = (event : any) => this.imagemUrl = event.target.result;
    this.file = ev.target.files;
    reader.readAsDataURL(this.file[0]);
    this.uploadImagem();
  }

  uploadImagem(): void{
    this.anuncioservice.postUpload(this.file)
    .subscribe(
      (fileName) => {
        this.nomeArquivo = fileName;
      },
      (error: any) => {
        alert('Erro ao enviar imagem para o servidor.');
      }
    )
  }

  inputChange(event: Event) {
    const files = (event.target as HTMLInputElement).files;
    this.arquivoSelecionado = files?.item(0);
    this.anuncioservice
      .EnviarArquivo(this.arquivoSelecionado!)
      .subscribe((nomeArquivo) => {
        this.nomeArquivo = nomeArquivo;
        this.localUrl = this.arquivoSelecionado;
        console.log(this.arquivoSelecionado);
      });
  }

  selectFile(event: any) {
    this.file = <File>event.target.files[0];
    if (event.target.files && event.target.files[0]) {
      var reader = new FileReader();
      reader.onload = (event: any) => {
        this.localUrl = event.target.result;
      };
      reader.readAsDataURL(event.target.files[0]);
    }
  }
}
  <div class="container card col-lg-6 mt-3">
    <div class="col-lg-12" style=" padding: 25px; ">
      <div class="card-header text-center" style="background-color: darkblue; color: white;">
        <h4>Cadastro de Anúncio</h4>
      </div>
      <form (ngSubmit)="salvarAnuncio()" [formGroup]="form">
        <div class="card-body row">
          <div class="form-group col-md-12">
            <label for="descricao">Descrição</label>
            <input
              type="text"
              class="form-control"
              formControlName="descricao"
              placeholder="Digite a descrição do seu anúncio"
            />
            <div *ngIf="f.descricao.errors?.required" class="invalid-feedback"> Descrição é obrigatória</div>
            <div *ngIf="f.descricao.errors?.minlength" class="invalid-feedback"> Descrição deve ter no mínimo 4 caracteres</div>
            <div *ngIf="f.descricao.errors?.maxlength" class="invalid-feedback"> Descrição deve ter no máximo 150 caracteres</div>
          </div>
          <div class="form-group col-md-4">
            <label>Válido até</label>
            <input type="date" class="form-control" formControlName="data_validade" />
            <div *ngIf="f.data_validade.errors?.required" class="invalid-feedback"> Escolha uma data de validade para o anúncio</div>
          </div>
          <div class="form-group col-md-4">
            <label for="inputState">Categoria</label>
            <select id="categoriaId" class="form-control" formControlName="categoria">
              <option selected>Selecione...</option>
              <option *ngFor="let categoria of categorias" value="{{ categoria.id }}">
                {{ categoria.descricao }}
              </option>
            </select>
            <div *ngIf="f.categoria.errors?.required" class="invalid-feedback"> Escolha uma categoria para o anúncio</div>
          </div>
          <div class="form-group col-md-4">
            <label for="preco">Preço</label>
            <input
              type="text"
              class="form-control"
              formControlName="preco"
              id="preco"
              placeholder="Ex: R$23,99"
            />
            <div *ngIf="f.preco.errors?.required" class="invalid-feedback"> Defina um preço para o anúncio</div>
          </div>
            <div class="form-group row">
              <label class="col-md-4" for=""> Selecione a Imagem do Produto</label>
              <input type="file" #file (change)="onFileChange($event)" style="display: none" formControlName="imagem">
              <img class="image col-md-4" [src]="imagemUrl" (click)="file.click()" style="width: 20%" tooltip="Upload de nova Imagem">
              <div *ngIf="f.imagem.errors.required" class="invalid-feedback"> Escolha uma imagem para o anúncio</div>
            </div>
      </div>
    </form>
        <div class="card-footer" style="margin: 10px">
          <button class="btn btn-dark" style="margin-right: 15px" [routerLink]="['/gerencial-empresa']">
            Voltar
          </button>
          <button type="submit" class="btn btn-success">Salvar</button>
        </div>
    </div>
  </div>
