import { AnuncioService } from 'src/app/Services/AnuncioService';
import { Component, OnInit } from '@angular/core';
import { Anuncio } from 'src/app/Models/Anuncio';
import { NgxSpinnerService } from 'ngx-spinner';
import { FormControl } from '@angular/forms';
import { __values } from 'tslib';
import { tap, map, filter, distinctUntilChanged, debounceTime, switchMap} from 'rxjs/operators';


@Component({
  selector: 'app-pesquisa',
  templateUrl: './pesquisa.component.html',
  styleUrls: ['./pesquisa.component.css'],
})
export class PesquisaComponent implements OnInit {

  descricao: string = "";
  public anuncios: Anuncio[] = [];
  queryFiled = new FormControl();

  constructor(
    private anuncioService: AnuncioService,
    private spinner: NgxSpinnerService) {}

  ngOnInit(): void {

  }

  buscarAnunciosPorDescricao() {
    debugger;

    this.spinner.show();
    this.anuncioService.BuscarTodosAnunciosOuPorDescricao(this.descricao).subscribe(
      (result: any) => {
        debugger;
        if (result != null) this.anuncios = result;
        setTimeout(() =>{
          this.spinner.hide();
        }, 500);
      },
      (erro) => {
        alert('Erro ao buscar os anúncios.');
        this.spinner.hide();
      }
    );
  }
}
